const p=()=>{const u=Array.from(document.querySelectorAll("[data-carousel-parent][data-featured-listing]"));for(const n of u){let t=n.emblaApi,l=t?.slideNodes(),o=t?.selectedScrollSnap();const d=e=>{if(l===void 0){console.debug("selectCurrentSlide was used before carousel was initialized");return}for(let a=0;a<l.length;a++){const r=l[a];if(a===e)gsap.to(r,{scale:1,x:0,ease:"back",duration:.7});else{const s=a<e,c=s?e-a-1:a-e-1,m=s?"right":"left";r.style.transformOrigin=`${m} center`,gsap.to(r,{x:()=>{const i=Number.parseFloat(getComputedStyle(document.documentElement).getPropertyValue("--featured-animation-morph-x-percent"));return s?`${i*c}%`:`-${i*c}%`},scale:()=>getComputedStyle(document.documentElement).getPropertyValue("--featured-animation-scale"),ease:"back",duration:.7})}}};n.addEventListener("embla:init",e=>{t=e.detail.embla,l=t.slideNodes(),o=t.selectedScrollSnap(),d(o)}),n.addEventListener("embla:select",e=>{t=e.detail.embla,o=t.selectedScrollSnap(),d(o)})}};p();
